mapper.select:
select
id, username, password, gender, birthday, is_delete
from sys_user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and gender = #{gender}


        and birthday = #{birthday}


        and is_delete = #{isDelete}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sys_user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and gender = #{gender}


        and birthday = #{birthday}


        and is_delete = #{isDelete}
